import Projets from './Projets.js';

import 'bootstrap/dist/css/bootstrap.min.css';

function Header() {

    function project() {
        var toShow = document.getElementsByClassName('mesProjets')[0]
        var toHide = document.getElementsByClassName('plusSurMoi')[0]
        console.log('porje')
        toShow.style.display = 'flex !important';
        toHide.style.display = 'none !important';
    }
    function info() {
        var toHide = document.getElementsByClassName('mesProjets')[0]
        var toShow = document.getElementsByClassName('plusSurMoi')[0]
        console.log(toHide)

        toShow.style.display = 'flex !important';
        toHide.classList.remove('')
        toHide.classList.add('')

    }

    return (
        <header className="App-header">
            <h1>Lucas Aptel</h1>
            <h2>DÃ©vellopeur Web Junior</h2>
            <div class='container-fluid d-flex'>
                <div class='col-4'>
                    <div className="d-grid gap-2">
                        <Button variant="primary" onClick={info} size="lg">
                            En savoir plus sur moi
                        </Button>
                    </div>
                    <div className="d-grid gap-2 plusSurMoi">
                        <Infos></Infos>
                    </div>
                </div>
                <div class='col-4'>

                </div>
                <div class='col-4'>
                    <div className="d-grid gap-2">
                        <Button variant="secondary" onClick={project} size="lg">
                            Voir mes projets
                        </Button>
                    </div>
                    <div className="d-grid gap-2 mesProjets">
                        <Projets></Projets>
                    </div>
                </div>

            </div>
        </header>
    );
}

export default Header;
